import React from "react"
import './newsNav.css'

export const NewsNav = (props) => {
    const elements = props.topicsList.map(item => {
        return (
            <li>
                <a href = "#"
                onClick={async () => {
                    const response = await fetch("/change_topic", {
                      method: "POST",
                      headers: {
                        "Content-Type": "application/json"
                      },
                      body: JSON.stringify({topic: item})
                    }).then(
                      res => res.json()
                    ).then(
                      data => {
                        console.log(data.articles)
                        props.onNewTopic(data);

                      }
                    );


                  }}>{item}</a>
            </li>
        );
    });
    return(

      <div class="dropdown">
        <button class="dropdown-btn" aria-label="menu button" aria-haspopup="menu" aria-expanded="false" aria-controls="dropdown-menu">
          <span>Topics</span>
          <span class="arrow"></span>
        </button>
        <ul class="dropdown-content" role="menu" id="dropdown-menu">
          {elements}
        </ul>
      </div>


        /*<div class="css-umysuv">
            <ul class="css-397oyn">
                {elements}
            </ul>
        </div>*/
    )

};
