import React, { useState, useEffect } from 'react'
import NewsItem from "./components/NewsItem"
import './App.css';

function App(){
  const[data, setData] = useState([])

  useEffect(() => {
    fetch("/sentiment").then(
      res => res.json()
    ).then(
      data => {
        setData(data.articles)


      }
    )

  }, [])
  function topicChange(){
    fetch("/queryTopic").then(
      res => res.json()
    ).then(
      data => {
        setData(data.articles)

      }
    )
  }


  console.log(data.urlToImage);

  var mapped = data.map(article => <NewsItem 
    author={article.author}
    content={article.content}
    description={article.description}
    publishedAt={article.publishedAt}
    sentiment={article.sentiment}
    source={article.source.name}
    title={article.title}
    url={article.url}
    urlToImage={article.urlToImage}
  />);

  return (
    <main>
      <div>
      <form method='post' action='/queryTopic' onSubmit = {topicChange()}>
                    <input type="text" id='topic' name='topic' />
                    <input type='submit' value='submit' />
                </form>
      </div>
      <div className = "cards-container container flex">
         {mapped}
      </div>
    </main>

    )
}

export default App
