          <li>
              <a href = "#"
              onClick={async () => {
                  const response = await fetch("http://localhost:5050/articles/" +item + "/").then(
                    data => {
                      console.log(data.json());
                      if(data)
                      props.onNewTopic(data.json());

                    }
                  );


                }}>{item}</a>
          </li>
      );
    });
    return(

      <div class="dropdown">
        <button class="dropdown-btn"  
        onClick = {() => {
          setIsPressed(!isPressed)
          console.log(isPressed);
        }}>
          <span>Topics</span>
          <span class="arrow"></span>
        </button>
        {
          isPressed && 
          <ul class="dropdown-content" role="menu" id="dropdown-menu">
            {elements}
          </ul>
        }

        <script>

        </script>
      </div>


        /*<div class="css-umysuv">
